(window.webpackJsonp=window.webpackJsonp||[]).push([[33],{433:function(a,t,s){"use strict";s.r(t);var v=s(44),e=Object(v.a)({},(function(){var a=this,t=a.$createElement,s=a._self._c||t;return s("ContentSlotsDistributor",{attrs:{"slot-key":a.$parent.slotKey}},[s("altLang"),a._v(" "),s("h1",{attrs:{id:"frontmatter-title"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#frontmatter-title"}},[a._v("#")]),a._v(" "+a._s(a.$frontmatter.title))]),a._v(" "),s("p",[a._v("அமைதியான இடத்தில் அமர்ந்துகொண்டு மனதை அடக்க முற்படுங்கள். மனதை அடக்க மிக எளிய வழி அமைதியாக அமர்ந்து, சிறிது நேரம் அதை அதன் விருப்பபடி திரிய விடுவதே. எண்ணங்களை அடக்க எந்த முயற்சியும் செய்யதீர், அந்த அலையும் எண்ணங்களை மட்டுமே பின் தொடர வேண்டும். சற்று நேரத்தில் மனம் அமைதி அடைந்து விடும். இவ்வாறு 11 நிமிடம் செய்து வர அளவிட முடியாத ஆற்றல் பெருகுவதை உங்களால் உணர முடியும்.")]),a._v(" "),s("h2",{attrs:{id:"தியானம்-meditation-and-values"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானம்-meditation-and-values"}},[a._v("#")]),a._v(" தியானம் (Meditation and Values)")]),a._v(" "),s("p",[a._v("தியானம் என்பது ஒரு கிளர்ச்சியூட்டும் அனுபவம், மலர்ச்சியூட்டும் பேரின்பம். இதுவரை நாம் அறிந்திராத மகத்தான அனுபவம். நினைவில்லாமல் நின்றால் தூக்கம், நினைவோடு நின்றால் அதுவே தியானம். தியனாத்தில் மெய்உணர்வு எனும் அறிவு மட்டும் விழித்திருப்பதால் தூங்காமல் தூங்கும் நிலையான அறிதுயில் என்ற நிலை ஏற்படுகிறது.")]),a._v(" "),s("h2",{attrs:{id:"தியானத்தில்-உடலியல்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானத்தில்-உடலியல்-மாற்றங்கள்"}},[a._v("#")]),a._v(" “தியானத்தில் உடலியல் மாற்றங்கள்”")]),a._v(" "),s("p",[a._v("“ஓய்வு” ஆழ்ந்த உறக்கத்தில் கிடைக்கும் மிக மிக ஆழ்ந்த ஓய்வை விட ஓர் அற்புத நிலை. அதனால் உடலில் பல மாற்றங்கள் நிகழ்கிறது. உதாரணம் : நம் உடலின் தசைநார்கள் நன்கு ஓய்வடைந்து இளைமையான தோற்றம் ஏற்படுகிறது.")]),a._v(" "),s("h2",{attrs:{id:"தியானத்தில்-மனவியல்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானத்தில்-மனவியல்-மாற்றங்கள்"}},[a._v("#")]),a._v(" தியானத்தில் மனவியல் மாற்றங்கள்:")]),a._v(" "),s("p",[a._v("பரந்த விழிப்புணர்வுடன் கூடிய நுண்ணறிவு திறன் மேம்படுகிறது.\n(IQ) மேம்பாடு 16 to 121 in 2 year’s – இல் நிகழ்கிறது.")]),a._v(" "),s("h2",{attrs:{id:"தியானத்தில்-சமூக-இயல்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானத்தில்-சமூக-இயல்-மாற்றங்கள்"}},[a._v("#")]),a._v(" தியானத்தில் சமூக இயல் மாற்றங்கள்:")]),a._v(" "),s("p",[a._v("இயற்கையை நேசித்தல் நிகழ்கிறது. மனித நேயம் உருவாகிறது. அன்புடன் கூடிய உடன்பாட்டு சிந்தனைகள் மேம்படுகிறது.")]),a._v(" "),s("p",[a._v("தியானத்தின் மூலம் நிரூபணம் செய்யப்பட்ட உண்மைகள் எராளமாக உள்ளது. குறிப்பாக மாணவர்களின் கற்கும் திறன் 2 முதல் 3 மடங்கு அதிகரிக்கிறது. மன உளைச்சல் நீங்கி, சீரான இரத்த அழுத்தம் ஏற்பட்டு உடலும் மனமும் ஆரோக்கியம் அடைகிறது.")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("“சுருதி, நூல், சாஸ்திர வேதாகமங்களெல்லாம் சுருக்கிச் சொல்லொன்றில் அடக்கிச் சுழிமுனயும் கடத்தி சும்மா இருக்கும் சுகமே உணர்வு.” \n    -\tபரஞ்சோதி மகான்.\n")])])]),s("p",[a._v("விழிப்புணர்வோடு நாம் செய்யும் செயல்கள் அனைத்தும் தியான அனுபவங்களே. ஆம் நாம் இக்காலகட்டத்தில் விழிப்புணர்வோடு சுவாசிப்பதும் இல்லை. விழிப்புணர்வோடு உண்பதும் இல்லை. விழிப்புணர்வோடு வாழ்வதும் இல்லை. இதனால் நாம் நம் உடல் மற்றும் மன ஆரோகியத்தினை இழந்து துன்பம் அடைகிறோம். நம் வாழ்வு மலர்ச்சியும் வளர்ச்சியும் அடையும் என்ற நம்பிக்கையில் இந்த ஆய்வு கட்டுரையை ஞானிகளின் நல்லாசியோடு உங்கள் முன் பணிவோடு சமர்பிக்கிறேன்.")]),a._v(" "),s("h2",{attrs:{id:"தியானம்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானம்"}},[a._v("#")]),a._v(" தியானம்:")]),a._v(" "),s("p",[a._v("“தியானம்” என்னை ஓர் அழகான சொல், இதைச் சொல்லும் போதே மனம் விவரிக்க முடியாத ஓர் அமைதி அடைகிறது. தியானம் நம்முடைய சொந்த இயல்பை, உண்மையான யதார்த்த உணர்வை உணரும் ஓர் அற்புத நிலை. நாம் விழிப்புணர்வோடு உறங்கும் நிலையே தியானம். உடல் சார்ந்த விதத்திலோ, மனம் சார்ந்த விதத்திலோ எதையும் செய்வதில்லை. எவ்வித நிகழ்வுமின்றி அனைத்துச் செய்கையும் நின்றுவிட நாம் சும்மாயிருக்கிறோம். அது நாம் செயாக்கூடியதல்ல. பயிற்சி பெறக் கூடியதுமல்ல. அதன் இயல்பை அறிந்து கொள்கிறோம் அவ்வளவுதான்.")]),a._v(" "),s("p",[a._v("இந்த உலக இயக்கதிற்கெல்லாம் எந்த ஒரு சக்தி காரணமோ அதே சக்திதான் நம் இயக்கத்திற்கும் காரணம் என்பதை அறியவைப்பதும் புரியவைப்பதும் தியானமே. பகவான் ரமண மகரிஷி நம்மை படைத்த சக்தியே உலகத்தையும் படைத்திருகிறது என இந்த கருத்தினை உறுதிபடுத்துகின்றார்.")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("தியானம் என்பது சாட்சியாயிருந்து கவனிப்பது\nதியானம் செய்தல் என்றாலே சாட்சியாதல் தான்\nதியானம் என்பது வெறும் பயிற்சி உத்தி அல்ல\nதியானம் என்பது புரிந்துகொள்ளும் நிலை.\n\n“தியான ஹேயாஸ்தத் வ்ருத்தய:”\n    -பதஞ்சலி யோக சூத்திரம் (11) சாதனா பாதம்\n")])])]),s("h2",{attrs:{id:"விளக்கம்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#விளக்கம்"}},[a._v("#")]),a._v(" விளக்கம் :")]),a._v(" "),s("p",[a._v("தியானம் செய்து அவற்றின் ஸ்தூல வடிவங்களை நீக்க வேண்டும் இவ்வாறு எழும் ஸ்தூல வடிவங்களினால் எழும் அலைகளை ஒடுக்க தியானம் ஒரு பெரிய துணையாக இருக்கும். தியானம் செய்யும் பொது மனமானது இந்த அலைகளை ஒழித்து விடும்.")]),a._v(" "),s("h2",{attrs:{id:"தியானத்தின்-அறிவியல்-பூர்வ-உண்மைகள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானத்தின்-அறிவியல்-பூர்வ-உண்மைகள்"}},[a._v("#")]),a._v(" தியானத்தின் அறிவியல் பூர்வ உண்மைகள்:")]),a._v(" "),s("p",[a._v("தியானம் என்பது நினைவு, கனவு, உறக்கம் அதாவது விழிப்பு நிலை (ஜாக்கிரதம்) கனவு நிலை (சொப்பனம்) உறக்க நிலை (சுழுப்த்தி) இதற்கெல்லாம் அப்பால் உள்ள மேயுனர்வான சுத்த பரவெளி (pure conscious) மட்டும் உள்ள நிலையே தியானம் என்பதாகும்.")]),a._v(" "),s("p",[a._v("இதனையே “முப்பாழுக்கும் அப்பால் சென்று, அப்பாலுக்கப்பால்” என்று பரஞ்சோதி மகன் எடுத்து இயம்புகின்றார். தியானம் அபரிமிதமான அளவில் ஓய்வு தருவதால் உடலுக்கும் மனதிற்கும் புத்துணர்ச்சி அளிக்கிறது. இந்த அழ்ந்த ஓய்வு உடல் உறுப்புகளின் முழுமையான மற்றும் தடையற்ற இயக்கத்திற்குத் தடையாக உள்ள அழுத்தமான உணர்ச்சி மற்றும் சோர்வை நீக்கி விடுவதால் கருவிகள் பத்தும் சீராக செம்மையாக முழுத்திறனுடன் இயங்கத் துணைபுரிகிறது.")]),a._v(" "),s("p",[a._v("மேலும், தியானம் நமக்கு சந்தோஷமான புத்துணர்வளிக்கும் அக விழிப்பு நிலையை (inner awareness) அளிக்கிறது. ஓய்வும், சுகணர்வும் (Rest and relaxation) இயல்பாக உறக்கத்தைத்தான் ஏற்படுத்தும். அனால் தியானத்தால் நாம் அனுபவிப்பது ஓய்வு நிறைந்த விழிப்புணர்வு நிலையாகும். (Restful alertness or reformed awarness).")]),a._v(" "),s("p",[a._v("விழிப்பு, கனவு, உறக்கம் என்ற இம்மூன்று நிலைகளை தாண்டி தியானம் கைகூடும் பொது நினைவு ஒடுங்கி, மிகமிகக் குறைந்த செயல்பாடு நிலையில் இருப்பதைத்தான் நான்காவது நிலை நினைவு என்று கூறப்படுகின்றது. இந்த மிகமிகக் குறைந்த செயல்பாடு நிலையில் நினவு(mind) இருக்கும்போது நாம் “தனி உணர்வு” (Pure consciousness) என்ற அனுபவம் பெறுகின்றோம்.")]),a._v(" "),s("p",[a._v("நாம் அனைவரும் தன உணர்வுடன் (pure conscious) இருந்தால் தான் எந்த ஒரு செயலும் செய்ய இயலும். நாம் வெற்றிடத்தைத்தான் கவனிகின்றோமேயன்றி நமது தன் உணர்வின் தரத்தை காணும் நிலையை பெரும்பாலும் முயலுவதில்லை.")]),a._v(" "),s("h2",{attrs:{id:"தியான-நுட்பம்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியான-நுட்பம்"}},[a._v("#")]),a._v(" தியான நுட்பம்:")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("மனத்தை அடக்குவது தியானமா? – இல்லை;\nமனத்தை ஆராய்வது தியனாமா? – இல்லை;\nமனத்தை அழிப்பது தியனாமா? – இல்லை;\nமனத்தைக் கடப்பது, மனத்திருக்கு அப்பால்;\nசெல்ல முயன்று, சென்றுவிடுவதே தியானம்.\n")])])]),s("p",[a._v("ஏன் மனதை அடக்குவது தியானமாகாது. நம்மால் முடியுமா மனதை அடக்க? பிரபஞ்சத்தில் மிக மிக வேகமானது ஒன்று உண்டென்றால் அது மனோ வேகம் தான். ஒரு நொடியில் அவ்வளவு விசயங்களை எண்ண வைக்கும். தன்னை அடக்கத் தானே துணை வருமா மனம். எனவே தான் “மனம் என்னும் குரங்கு” என்று பெரியோர்கள் கூறியுள்ளனர். மனத்தை ஆராய்வதும் தியானம் ஆகாது. மனதை அடக்க நினைத்தால் அலையும், அறிய நினைத்தால் அடங்கும்.")]),a._v(" "),s("p",[a._v("மனதை அழிப்பது – இது என்னை? ஆழ்ந்த உறக்கத்தில் மனம் எங்கு செல்கின்றது மனமும் உறங்குவதே ஆழ்ந்த உறக்கம். இதில் கனவுகள் இல்லை. சலனம் இல்லை. அனால் மனம் இல்லாவிடில் மற்றவையும் தோன்றாது.")]),a._v(" "),s("p",[a._v("அனால், மனம் தன்பாட்டிற்கு தன செயலைச் செய்து கொண்டிருகின்றபோதே, தன மனத்தாலேயே தன மனதைக் கடந்து, மனதின் செயல்பாடுகள், விகாரங்கள், எண்ண ஓட்டங்கள் அதனால் ஏற்படும் உடல் பௌதிக இரசாயன மாற்றங்கள் யாவற்றின் பாதிப்பும் தன்னையோ தான் கொண்டுள்ள உடலையோ பாதிக்கா வண்ணம், மனதிற்கு எட்டாத இடத்தில் மனதைத் தாண்டிச் செல்வது தான் தியானம்.")]),a._v(" "),s("p",[a._v("உதாரணமாக, சில துறவிகள் முழு நிர்வாணமாக எங்கும் உலவி வருவதைப் பார்கின்றோம். இன்றும் ஜைன கோவிலான “கோமதீஸ்வரர்” சன்னிதியில் அந்த மதத் துறவிகள் முழு நிர்வாணமாகவே வழிபாடுகளும் பூஜைகளும் செய்கின்றனர். ஏன்? மனதைக் கடந்தவர்கள் புறஉலக தோற்றங்கள் எந்தவொரு மாற்றத்தையும் ஏற்படுத்தாத அளவுக்கு மனதைக் கடந்தவர்களே அது போன்ற நிர்வாணமாக உலாவ இயலும்.")]),a._v(" "),s("h2",{attrs:{id:"தியானத்தின்-உண்மை-நிலை"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானத்தின்-உண்மை-நிலை"}},[a._v("#")]),a._v(" தியானத்தின் உண்மை நிலை:")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("“நினைவிடத்தில் நினைவை நினைத்து\nநினைவை நிறுத்தி நினைவை சுழற்றி\nநினைவில் நிலைத்து நினைவாய் நினைவில்,\nநினைவோடு நின்ற நினைவும் நானே”\n-\tபரஞ்சோதி மகான்\n")])])]),s("p",[a._v("மனதை மனதாலேயே கடந்து தாண்டிச் சென்று மனதை மனது பார்ப்பது தான் தியானம். ஆக மனதை ஆராயாது, மனதை அடக்காது, மனதை அழிக்காது மனத்தோடு மனமாய் மனதைத் தாண்டி மனதைப் பார்க்க முயல வேண்டும். ஆற்றைக் கடப்பவன் எதிர்த்து நீந்தாமல், நீரோட்டத்தின் போக்கில் சென்று கடப்பது எளிது. அதுபோல, நினைவின் இடத்தை அறிய நினைவோடுதான் முயல வேண்டும்.")]),a._v(" "),s("p",[a._v("அறிவிற் பெரியோர்களாகிய மகான்கள் போன்றவர்கள் சொல்லிய ஒவ்வொரு சொல்லும் ஆயிரம் அர்த்தங்கள் கொண்டவை. திருவாசகம், சிவபுராணம் போன்று அவரவர் நிலைக்குத் தக்கவாறு அனைத்து சிஷ்யர்கட்கும், குரு சிஷ்ய பாரம்பரியத்தில் வழி வழியாக வரும் அனைவருக்கும் அவரவர் நிலைக்குத்தக்ககவாறு பொருள் விளங்கும்.")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("“ஆங்காரம் உள்ளடக்கி ஐம்புலனைச் சுட்டறுத்துத் \n\tதூங்காமல் தூங்கிச் சுகம் பெறுவதெக்காலம்”\n")])])]),s("p",[a._v("தியானம் என்பது மொழி, மதம் கடந்த ஒரு உணர்ச்சி. அதை நாம் உணர வேண்டுமே அன்றி, கேள்வியும் பதிலுமாக இருப்பது தியானமன்று. உதாரணமாக பலநாள் பட்டினி கிடப்பவன் தனது உடலில் உள்ள அனைத்து செல்களிலும் பசியை உணருவான். எவ்வாறு மிதமிஞ்சிய களைப்பை நாம் உடல் முழுவதும் உணருகின்றோமோ, எவ்வாறு கடுமையான தாகத்தை உடல் முழுவதும் உணருகின்றோமோ அது போன்று இருப்பு சக்தியின் இருப்பை நமது மூன்று உடல்களிலும் அனுபவமாக உணர வேண்டும். உணர முயல வேண்டும். அப்படி அனுபவத்தில் தெரிந்தவரே நன்கு தியானம் செய்து சாதகம் செய்தவர் என்று கூறினால் மிகையாகாது.")]),a._v(" "),s("p",[a._v("இந்த இருப்பு சக்தியை ஆதாரமாகக் கொண்டு தான் நமது அன்றாட இயக்க சக்தியின் நிகழ்வுகள், இயக்க சக்தியின் நிலைக்களமாக மாறுவது நமது உறக்கம். ஆழ்ந்த உறக்கத்தில் சுமார் 40 நிமிடம் இருந்தால் தான் அமிர்தம் (NECTOR) சுரக்கும். அந்த அமிர்தம் தான் “Growth Hormone” என்ற ஹார்மோன். பிறந்த குழந்தை முதல் வருடத்தில் அடையும் வளர்ச்சியை அதன் வாழ்நாளில் என்றும் அடைவிதில்லை. அதிலும் மேலாக ஒரு செல் கரு, பத்து மாதத்தில் குழந்தையாக வளருவது மிகமிக அதிகமான வளர்ச்சி.")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("“குழந்தை தூங்க தூங்க வளர்ச்சி” – பழமொழி\n")])])]),s("p",[a._v("கவனித்துப் பார்த்தல் பிறந்த குழந்தை பாலுக்கு விழிக்கும். ஊட்டியபின் துயிலும். தூக்கமும் நல்ல ஆழ்ந்த நித்திரை. எனவே Growth Hormone அதிகம் சுரந்து அதிவேக வளர்ச்சி.")]),a._v(" "),s("p",[a._v("ஆழ்ந்த தியானத்திலும் இதே நிகழ்வுதான். அதாவது அப்போது Growth Hormone நன்கு சுரக்கும். அதுவே அன்றைய வேலைக்குத் தேவையான சக்தியினை அளிக்கும். மனிதனின் வயது கூடக்கூட அவனது உறக்கம் குறைந்து கொண்டே வருவது இயற்கை. உறக்கம் குறையக் குறைய, “அமிர்தம்” (Growth Hormone) சுரப்பதும் குறைந்து, வளர்ச்சி மாற்றத்தில் வளர்ச்சி விகிதம் மிகமிகக் குறைவுற்று சிதைவு மேலோங்கும் தன்மை வந்து விடுகிறது. இதனால் விரைவாக உடல் மூப்பு அடைகிறது.")]),a._v(" "),s("p",[a._v("ஆழ்ந்த உறக்கத்தில் நாம் நமது “காரணத்தில்” ஒடுங்குகிறோம். இது நமது ஜீவா அறிவுக்குப் புலப்படாமல் அன்றாடம் நிகழ்ந்து வரும் நிகழ்ச்சியாகும். நம்மை அறியாமல் மனதைத் தாண்டிய காரணத்தில் ஒடுங்கும்போது, ஒடுங்கிய நிலையிலேயே சுமார் 40 நிமிடம் இருந்தால் தான் Growth Hormone சுரக்கும். சுரப்பதோ மிக்ரோகிராமிலும் 1000ல் ஒரு பங்கு. “கடுகு சிறுத்தாலும் காரம் குறையாது போலும். மூர்த்தி சிறிது கீர்த்தி பெரிது” என்பது போலும். மிகக் குறைந்த அளவேயானாலும் அதுதான் எல்லா ஜீவராசிகளையும் அடுத்த நாளுக்குத் தயார் செய்வதும், தேவையான சக்தியைத் தருவதும் ஆகும். ஆழ்ந்த நித்திரையில் ஐம்புலன்கள், மனம், அறிவு யாவும் ஒடுங்கி விடுகின்றது. இந்த அற்புதங்கள் தியானத்தால் மேன்மேலும் நடந்து உடல் மூப்பினை  அடையாமல் காக்கிறது. அதே சமயம் மீண்டும் பிறவா நிலையையும் அளிக்கிறது.")]),a._v(" "),s("h2",{attrs:{id:"தியானத்தின்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#தியானத்தின்-மாற்றங்கள்"}},[a._v("#")]),a._v(" தியானத்தின் மாற்றங்கள்:")]),a._v(" "),s("p",[a._v("உடலியல் மாற்றங்கள், மனவியல் (உளவியல்) மாற்றங்கள், சமூக இயல் மாற்றங்கள், உயிர் வேதியியல் மாற்றங்கள் என விரிந்து கொண்டே செல்கின்றது.")]),a._v(" "),s("h3",{attrs:{id:"i-உடலியல்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#i-உடலியல்-மாற்றங்கள்"}},[a._v("#")]),a._v(" I.\tஉடலியல் மாற்றங்கள்:")]),a._v(" "),s("ul",[s("li",[s("p",[a._v("“ஓய்வு” ஆழ்ந்த உறக்கத்தில் கிடைக்கும் மிகமிக ஆழ்ந்த ஓய்வை விட ஓர் அற்புத நிலை இந்த “ஓய்வு”. அதனால் உடலில் பல மாற்றங்கள் நிகழ்கிறது. உதாரணம்: நம் உடலின் தசைநார்கள் நன்கு ஓய்வடைந்து இளைமையான தோற்றம் ஏற்படுகிறது.")])]),a._v(" "),s("li",[s("p",[a._v("ஆரோக்கியம் கூடுகின்றது. போதைப் பொருட்களின் மீது நாட்டம் அறவே நீங்குகின்றது.")])]),a._v(" "),s("li",[s("p",[a._v("மூப்பு தடை செய்யப்படுகின்றது. தோற்றத்திலும், செயலிலும் உண்மையான வயதை விட 12 வயது வரை குறைவது நிருபணமாகியுள்ளது.")])])]),a._v(" "),s("h3",{attrs:{id:"ii-மனவியல்-உளவியல்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#ii-மனவியல்-உளவியல்-மாற்றங்கள்"}},[a._v("#")]),a._v(" II.\tமனவியல் (உளவியல்) மாற்றங்கள்:")]),a._v(" "),s("ul",[s("li",[a._v("பரந்த விழிப்புணர்வுடன் கூடிய நுண்ணறிவு திறன் மேம்படுகிறது.")]),a._v(" "),s("li",[a._v("(IQ) மேம்பாடு 16 to 121 in 2 year’s – இல் நிகழ்கிறது.")]),a._v(" "),s("li",[a._v("மனத்தின் செயல்பாடு சீராகவும், செம்மையாகவும் நிகழ்கின்றது.")]),a._v(" "),s("li",[a._v("சுயக்கட்டுப்பாடும், தன்னையறியும் திறனும் கூடுகின்றது.")])]),a._v(" "),s("h3",{attrs:{id:"iii-சமூக-இயல்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#iii-சமூக-இயல்-மாற்றங்கள்"}},[a._v("#")]),a._v(" III.\tசமூக இயல் மாற்றங்கள்:")]),a._v(" "),s("ul",[s("li",[s("p",[a._v("இயற்கையை நேசித்தல் நிகழ்கிறது. மனித நேயம் உருவாகிறது. அன்புடன் கூடிய உடன்பாட்டு சிந்தனைகள் மேம்படுகிறது. தியானத்தின் மூலம் நிரூபணம் செய்யப்பட்ட உண்மைகள் ஏராளமாக உள்ளது. குறிப்பாக மாணவர்களின் கற்கும் திறன் இரண்டு முதல் மூன்று மடங்கு அதிகரிகின்றது. மனஉளைச்சல் நீங்கி, சீரான தூக்கம், சீரான இரத்த அழுத்தம் ஏற்பட்டு உடலும் மனமும் ஆரோக்கியம் அடைகிறது.")])]),a._v(" "),s("li",[s("p",[a._v("குற்றங்கள், வன்முறைகள், பயங்கரவாதங்கள் குறைகின்றது.")])])]),a._v(" "),s("h3",{attrs:{id:"iv-உயிர்-வேதியியல்-மாற்றங்கள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#iv-உயிர்-வேதியியல்-மாற்றங்கள்"}},[a._v("#")]),a._v(" IV.\tஉயிர் வேதியியல் மாற்றங்கள்:")]),a._v(" "),s("ul",[s("li",[s("p",[a._v("தியானத்தின் போது இரத்தத்தில் லாக்டிக் அமிலம் அபரிமிதமாகக் குறைகின்றது. இந்த லாக்டிக் அமில அளவு குறைவது நிரூபணம் ஆனதன் மூலம் தியானம் என்பது உடலியலுக்கும் உளவியலுக்கும் உள்ள உறவு மற்றும் தொடர்புகளை ஆராய்ந்து அறியும் அறிவாக விளங்குகிறது.")])]),a._v(" "),s("li",[s("p",[a._v("ஒடுக்கத்தில் விழிப்புணர்வு (wakefulness during deep rest) – தியானத்தின் பொது மிகக் குறைந்த செயல்பாட்டுடன் கூடிய அதிகமான விழிப்புணர்வு (ie – increased wakefulness and decreased activity of brain wave in EEG – during meditation) வருவதை EEG மூலம் நிருபித்துள்ளார்கள்.")])])]),a._v(" "),s("p",[a._v("தியானத்தின் போது delta, alpha அலைகள் பதிவு மிகச் சாதாரணமாக கிடைப்பதால் தியானம் செய்பவர் இரண்டு நிலைகளையும் அதாவது (விழிப்பு – உறக்கம்) ஒரே சமயத்தில் அனுபவிகிறார் என்பது நிரூபணமாகிறது. அதாவது “அறிதுயில்” என்பது நிரூபிக்கப்பட்டு விட்டது.")]),a._v(" "),s("h3",{attrs:{id:"மேலும்-நிரூபிக்கப்பட்ட-உண்மைகள்"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#மேலும்-நிரூபிக்கப்பட்ட-உண்மைகள்"}},[a._v("#")]),a._v(" மேலும் நிரூபிக்கப்பட்ட உண்மைகள்:")]),a._v(" "),s("ul",[s("li",[a._v("மனஉளைச்சல் நீங்கி தெளிவு பிறக்கின்றது.")]),a._v(" "),s("li",[a._v("தாறுமாறாக இயங்கிக் கொண்டிருந்த மின்னோட்டம் மூளையில் சமனப்பட்டு ஒருங்கிணைந்து ஒத்திசைவுடன் கூடிய மின்னோட்டமாக மாறுகிறது.")])]),a._v(" "),s("h2",{attrs:{id:"முடிவுரை"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#முடிவுரை"}},[a._v("#")]),a._v(" முடிவுரை:")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("அறிவின் பெருக்கத்தால் நீயும் நானும் நானே நான்\nமனதின் ஒடுக்கத்தில் நீயும் நானும் நானே நான்\nநினைவின் ஓடுக்கத்திலும் நீயும் நானும் நானே நான்\n")])])]),s("p",[a._v("மொத்தத்தில் தியானம் தொடர்ந்து செய்து வந்தால், எவ்வளவு விரைவில் சமாதி நிலையை அடைகிறமோ அவ்வளவு விரைவில் நமது எண்ணம், சொல், செயல்களில் இருள் மறைந்து ஒளிபெருகும், மெய்யல்லாதவை மறைந்து மெய் ஒளிரும். அழிபவைகளினின்று அழியாமை மலரும்.")]),a._v(" "),s("div",{staticClass:"language- extra-class"},[s("pre",[s("code",[a._v("    அஸத்தோமா... சத் கமயா\n    தமசோமா ஜோதிர் கமயா\n    ம்ருத்யோர்மா அம்ருதம்கமயா\n    ஓம் சாந்தி சாந்தி சாந்தி\n    ஆதலினால் தியானம் செய்வோம்\n    வாழ்வை வெல்வோம்\n    மகிழ்ச்சி!\n    இன்பமே சூழ்க! வாழ்வாங்கு வாழ்க!!\n")])])])],1)}),[],!1,null,null,null);t.default=e.exports}}]);